async function exampleMultiTest() {
  for (let idx=0; idx<continousCount; ++idx) {
    switch (idx % 4) {
      case 0:
        hashType = 'p2sh-p2wsh';
        break;
      case 1:
        hashType = 'p2sh-p2wpkh';
        break;
      case 2:
        hashType = 'p2wsh';
        break;
      case 3:
      default:
        hashType = 'p2wpkh';
        break;
    }
    await example();
    if (idx < (continousCount - 1)) {
      await sleep(continousSleep * 1000);
    }
  }
}

